import { useState } from 'react';
import {Link} from 'react-router-dom';
const ListContacts = ({handleClick , handleDelete, dispatchUser}) =>  {

  const [query,setQuery] = useState("");
  const updateQuery =(query)=>{
      setQuery(query.trim());
  }
  const userList = query=== "" ? contacts : contacts.filter((c) => c.name.toLowerCase().includes(query.toLowerCase()));
  const clearQuery = () => {
    updateQuery("");
  }
    return(
    <div className='list-contacts'>
      <div className='list-contacts-top'>
        <input className='search-contacts' type="text" placeholder='Search Contacts' value={query}
        onChange ={(e)=>updateQuery(e.target.value)} />
              <Link to="/create" className="add-contact">Add Contacts</Link>

      </div>
      {
        userList.length !== contacts.length && (
          <div className='showing-contacts'>
            <span>Now Showing {userList.length} of {contacts.length}</span>
            <button className='show-all' onClick={()=> clearQuery()}>Show All</button>
          </div>
        )
      }
  <ol>
  {userList?.data?.map((user) => 
  <li key={user.id} className="contact-list-item">
     <div className='list-item' onClick={()=>{handleClick(user.id)}}>
                {/* <div className="pop" style={{display : currentUser === user.id && toggle?'':'none'}}> */}
                    <button className="pop-btn" onClick={()=>{handleDelete(user.id)}}>
                        Delete User
                    </button>
                    <button className="pop-btn" onClick={()=>{dispatchUser(user)}}>
                        View Details
                    </button>
                {/* </div> */}
                    {user.id}
                    {user.name}
                    {user.phone}
            </div>
            </li>
            )}
    {/* <button className="add-contact" onClick={ ()=>addUser()}>Add User</button> */}
    <Link to="/create" className="add-contact">Add Contacts</Link>


    </ol>
   </div>
  )
} 


export default ListContacts;
