import "../css/App.css";
import { useState,useEffect } from "react";
import ListContacts from "./ListContacts";
import CreateContact from "./CreateContact";
import * as ContactsAPI from "../utils/ContactsAPI";
const App = () => {
  const removeContact = (person) =>{
    ContactsAPI.remove(person);
    setContacts(contacts.filter((c)=> c.id !== person.id))
  }
  const [screen,setScreen] = useState("list");
  const [contacts, setContacts] = useState([]);
    useEffect(()=>{
      const getContacts = async () => {
        const res = await ContactsAPI.getAll();
        setContacts(res);
      };
      getContacts();
    },[]);
 
  return(
    // <div>
    //   {
    //   screen==="list"&&<ListContacts contacts={contacts} onDeleteContact={removeContact} onNavigate={()=>setScreen("create")}/>
    //  }
    //  {
    //   screen ==="create" && <CreateContact/>
    //  }
    // </div>
    <Routes>
      <Route exact path="/" element={
        <ListContacts contacts={contacts} onDeleteContact={removeContact}/>
      } />
      <Route path="/create" element={
        <CreateContact/>
      }/>
    </Routes>
  )
};

export default App;
