import { create } from 'domain';
import PropTypes from 'prop-types';
import { useState } from 'react';
const ListContacts = ({contacts , onDeleteContact,onNavigate}) =>  {
  const [query,setQuery] = useState("");
  const updateQuery =(query)=>{
      setQuery(query.trim());
  }
  const showingContacts = query=== "" ? contacts : contacts.filter((c) => c.name.toLowerCase().includes(query.toLowerCase()));
  const clearQuery = () => {
    updateQuery("");
  }
    return(
    <div className='list-contacts'>
      <div className='list-contacts-top'>
        <input className='search-contacts' type="text" placeholder='Search Contacts' value={query}
        onChange ={(e)=>updateQuery(e.target.value)} />
              <a onClick={()=>{onNavigate}} className="add-contact">Add Contacts</a>

      </div>
      {
        showingContacts.length !== contacts.length && (
          <div className='showing-contacts'>
            <span>Now Showing {showingContacts.length} of {contacts.length}</span>
            <button className='show-all' onClick={()=> clearQuery()}>Show All</button>
          </div>
        )
      }
  <ol>
  {showingContacts.map((person) => 
  <li key={person.id} className="contact-list-item">
    <div
     className="contact-avatar" 
    style= {{
        backgroundImage: `url(${person.avatarURL})`}} >
    </div>
    <div className="contact-details">
        <p>{person.name}</p>
        <p>{person.handle}</p>
    </div>
    <button className="contact-remove" onClick={ ()=>onDeleteContact(person)}>Remove</button>
    </li>)}

    </ol>
   </div>
  )
} 

// ListContacts.PropTypes = {
//   contacts: PropTypes.array.isRequired,
//   onDeleteContact: PropTypes.func.isRequired,
// };
export default ListContacts;
