import "../css/App.css";
import { useState,useEffect } from "react";
import ListContacts from "./ListContacts";
import CreateContact from "./CreateContact";
import { useSelector,useDispatch } from 'react-redux'
import { Routes, Route, useNavigate} from "react-router-dom";
// import * as ContactsAPI from "../utils/ContactsAPI";
import { setData,fetchUserDetails,deleteUser } from '../redux/slices/viewDetails'
import Details from "./Details";

const App = () => {
  let navigate = useNavigate();
  const dispatch = useDispatch();
  const userList = useSelector((state)=>state.viewDetails)
 const [toggle,setToggle] = useState(false);
 const [currentUser,setCurrentUser] = useState(0)


  // const removeContact = (person) =>{
  //   ContactsAPI.remove(person);
  //   setContacts(contacts.filter((c)=> c.id !== person.id))
  // }
  // const createContact =(person)=>{
  //   const create = async () => {
  //     const res = await ContactsAPI.create(person);
  //     setContacts(contacts.concat(res));
  //   };
  //   create();
  //   navigate("/");
  // };

  
    useEffect(()=>{
      if(userList.firstTime){
        dispatch(fetchUserDetails())
        }
    },[]);
    const handleClick = (userId)=>{
      setToggle(!toggle)
      setCurrentUser(userId)
  }
  const dispatchUser = (userData)=>{
      dispatch(setData(userData))
      navigate(`/viewDetails/userId=${userData.id}`)
  }
  const handleDelete = (userId)=>{
      dispatch(deleteUser(userId))
  }
 
  return(
    <Routes>
        <Route exact path="/" element={
          <ListContacts handleDelete={handleDelete()} handleClick={handleClick()} dispatchUser={dispatchUser()}/>
        } />
        <Route path="/create" element={
          <CreateContact />
        } />
     <Route path='/viewDetails/:id' element={<Details/>} />

    </Routes>
  )
};

export default App;
